import requests

url = "http://10.5.5.9:8080/gopro/camera/presets/update_custom"

payload = {
    "custom_name": "string",
    "icon_id": 0,
    "title_id": 0
}
headers = {"content-type": "application/json"}

response = requests.request("PUT", url, json=payload, headers=headers)

print(response.text)

OkHttpClient client = new OkHttpClient();

MediaType mediaType = MediaType.parse("application/json");
RequestBody body = RequestBody.create(mediaType, "{\"custom_name\":\"string\",\"icon_id\":0,\"title_id\":0}");
Request request = new Request.Builder()
  .url("http://10.5.5.9:8080/gopro/camera/presets/update_custom")
  .put(body)
  .addHeader("content-type", "application/json")
  .build();

Response response = client.newCall(request).execute();

CURL *hnd = curl_easy_init();

curl_easy_setopt(hnd, CURLOPT_CUSTOMREQUEST, "PUT");
curl_easy_setopt(hnd, CURLOPT_URL, "http://10.5.5.9:8080/gopro/camera/presets/update_custom");

struct curl_slist *headers = NULL;
headers = curl_slist_append(headers, "content-type: application/json");
curl_easy_setopt(hnd, CURLOPT_HTTPHEADER, headers);

curl_easy_setopt(hnd, CURLOPT_POSTFIELDS, "{\"custom_name\":\"string\",\"icon_id\":0,\"title_id\":0}");

CURLcode ret = curl_easy_perform(hnd);

const request = require('request');

const options = {
